abstract class BaseComponent&lt;T extends HTMLElement = HTMLElement&gt;;

interface BaseComponentOptions {
	template?: string;
	mountTarget?: string | HTMLElement;
	tagName?: keyof HTMLElementTagNameMap;
	trustLevel?: TrustLevel // 'strict' | 'normal' | 'trusted' Default: 'normal'
}

// children registration
public childConfigs(): ComponentConfig[];

interface ComponentConfig {
	selector: string; // data-component attribute value
	factory: (element: HTMLElement) =&gt; BaseComponent;
}

// returns ComponentConfig[] ready for childConfigs() from an array
// registration is still handled by childConfigs().
protected catalogConfig(config: CatalogConfig): ComponentConfig[];

interface CatalogConfig {
	array: CatalogItemConfig[];
	elementName: string;
	elementTag?: keyof HTMLElementTagNameMap;
	selector: string; // data-catalog attribute value
	component: new (el: HTMLElement, data: any) =&gt; BaseComponent;
}
interface CatalogItemBase { index: number; }
type CatalogItemObject&lt;T extends object&gt; = T & CatalogItemBase;
interface CatalogItemPrimitive extends CatalogItemBase { value: string | number | boolean | null | undefined }
// When using catalogConfig : shape of templateFn argument (in Component subclass)
type CatalogItemConfig&lt;T = any&gt; = T extends object ? CatalogItemObject&lt;T&gt; : CatalogItemPrimitive;

// exposed methods
public destroy(): void;
public render(): HTMLElement;
