abstract class Component&lt;T extends HTMLElement = HTMLElement, K = any&gt; extends BaseComponent&lt;T&gt;;

interface ComponentOptions&lt;K = any&gt; extends BaseComponentOptions {
	// object or primitive value to use in HTML template
	config?: K;
	props?: ComponentProps; // className?: string; style?: string, [key: string]: any;
	defaults?: Partial&lt;K&gt;;
	templateFn?: (config?: K) =&gt; string;
	normalizeKeys?: (keyof K)[];
}

// template arrow function
const templateFn = (): string =&gt; ``;
const templateFn = (config: MyObject): string =&gt; ``;
// type CatalogItemConfig&lt;T = any&gt; = T extends object ? CatalogItemObject&lt;T&gt; : CatalogItemPrimitive;
const templateFn = (config: CatalogItemConfig&lt;MyObject&gt;): string =&gt; ``; // config.myObjprop, config.value, config.index
