// html
&lt;div data-catalog="my-catalog"&gt;&lt;/div&gt;

class MyParent {
	childConfigs(): ComponentConfig[] {
	// catalogConfig inherited method returns ComponentConfig[]
		const catalog =  this.catalogConfig({
			array: myArray, // Array of elements with the same shape as component's 'config', from which components will be generated
			elementName: 'first-element',
			selector: `my-catalog`,
			component: MyComponent
		});

		return catalog;
	}
}
