abstract class Service&lt;T extends Service&lt;T&gt;&gt;;

// For async and custom cleanup operations
protected onDestroy&lt;T extends Service&lt;T&gt;&gt;(this: () =&gt; T): void;

// singleton pattern
// expose instance
public static getInstance&lt;T extends Service&lt;T&gt;&gt;(this: new () =&gt; T): T;
public static createInstance&lt;T extends Service&lt;T&gt;&gt;( constructor: new (...args: any[]) =&gt; T, ...args: any[] ): T;

// cleanup
public static async destroy&lt;T extends Service&lt;T&gt;&gt;( this: new (...args: any[]) =&gt; T ): Promise&lt;void&gt;;
public static async destroyAll(): Promise&lt;void&gt;;
protected registerCleanup(callback: () => void)

// exposed methods for testing
// Resets instance without cleanup.
public static testingReset&lt;T extends Service&lt;T&gt;&gt;( this: new (...args: any[]) =&gt; T ): void;
public static testingResetAll(): void;
