type CoreEventMap = {
	'navigate': string;
	'reload': string;
	'view-mount': string;
	'view-render': string;
	'view-unmount': string;
	'track-pageview': { page: string };
	// Add other events and their payload types here
};

type StoreEventMap = {[ K in `store:${string}` ]: any; };

// Merged event map
type EventMap = CoreEventMap & StoreEventMap;
type EventKey = keyof EventMap;

type Subscription&lt;K extends EventKey = EventKey&gt; = { event: K; callback: (data: EventMap[K]) =&gt; void; once?: boolean; };

public subscribe&lt;K extends EventKey&gt;( event: K, callback: (data: EventMap[K]) =&gt; void ): void;
public emit&lt;K extends EventKey&gt;(event: K, data?: EventMap[K]): void;
public off&lt;K extends EventKey&gt;( event: K, callback?: (data: EventMap[K]) =&gt; void ): void;
public once&lt;K extends EventKey&gt;( event: K, callback: (data: EventMap[K]) =&gt; void ): void;
