// from src/app/languages.ts
export const LANGUAGES = ['en'] as const; // add languages here
export const DEFAULT_LANGUAGE: Language = 'en' as const;
export type Language = typeof LANGUAGES[number];
export type Translations = Record&lt;string, any&gt;;

// based on browser language. Defaults to DEFAULT_LANGUAGE.
// will look for language key in components and views 'locales' folder (ex: locales/en.json)
public async loadTranslations(lang: Language): Promise&lt;void&gt;;
public translate(key: string, params?: Record&lt;string, string&gt;): string;
public get currentLanguage(): Language;
public async initTranslations(): Promise&lt;void&gt;;
public resetTranslations(): void;
