// route configuration
type RouteConfig = 
	// view constructor
	(() =&gt; View) 
	// object with guards
	| { view: () =&gt; View; guards?: RouteGuard[]; };

// usage in app/routes.ts
'/my-route': () =&gt; new MyView()
'/guarded-route': { view: () =&gt; new GuardedView(), guards: [] }
'/route-with-optional-param:param?': () =&gt; new ViewWithOptionalParam()
'/route-with-required-param:param': () =&gt; new ViewWithRequiredParam()

type RouteGuard = (params: Record&lt;string, string&gt;) =&gt; boolean | string | Promise&lt;boolean | string&gt;;
type GuardResult = { allowed: boolean, redirectTo?: string, viewConstructor?: () =&gt; View }
type Routes = Record&lt;string, RouteConfig&gt;;
