type StoreState = Record&lt;string, any&gt;;

// set value and calls AppEventBus.emit(`store:${key}`, value)
public set&lt;T = any&gt;(key: string, value: T): void;
// get value
public get&lt;T = any&gt;(key: string): T | undefined;
// calls AppEventBus.subscribe(`store:${key}`, callback)
public subscribe&lt;T = any&gt;(key: string, callback: (value: T) =&gt; void): void;
// clear store state
public clear(): void;
// calls AppEventBus.off(`store:${key}`, callback)
public unsubscribe&lt;T = any&gt;(key: string, callback: (value: T) =&gt; void): void;
