content:
// currency
currency (
	value: number, 
	currency = 'USD', 
	locale = 'en-US'
);

// date
date (
	value: string | number | Date,
	locale = navigator.language,
	format = 'long',
	time = false,
);

// number (toFixed)
number (value, decimals = 0) =&gt; {});

// string pipes
uppercase;
lowercase;    
capitalize;
capitalizeAll;
truncate (value, length = 50, suffix = '...');

// utility pipes
default (value, defaultValue = '');
json;
