content:
// styles/base/_variables.scss
// snackbar colours
$snack-success: rgb(76 175 80);
$snack-error: rgb(244 67 54);
$snack-info: rgb(33 150 243);
$snack-default: rgb(153 153 153);

// styles/core/_libs.scss
.app-snackbar {
	position: fixed;
	left: 50%;
	transform: translateX(-50%);
	background: $snack-default;
	color: white;
	padding: 0.75rem 1.5rem;
	border-radius: 0.5rem;
	box-shadow: 0 2px 6px rgba(0,0,0,0.3);
	display: flex;
	gap: 1rem;
	align-items: center;
	animation: fade-in 0.3s ease;
}

.app-snackbar--bottom { bottom: 1rem; }
.app-snackbar--top { top: 1rem; }

.app-snackbar--success { background: $snack-success; }
.app-snackbar--error { background: $snack-error; }
.app-snackbar--info { background: $snack-info; }

.app-snackbar button {
	background: transparent;
	border: none;
	color: #fff;
	font-weight: bold;
	cursor: pointer;
}

@keyframes fade-in {
	from { opacity: 0; transform: translateX(-50%) translateY(10px); }
	to { opacity: 1; transform: translateX(-50%) translateY(0); }
}
