type NotifyOptions = {
	type?: 'info' | 'success' | 'error'; // default: 'info'
	position?: 'top' | 'bottom'; // default: 'bottom'
	duration?: number; // ms. default: 3000
	actionText?: string;
	onAction?: () =&gt; void;
};
function notify(message: string, options: NotifyOptions = {})

// usage
import { notify } from 'relative/path/to/libs/index.js';

notifyStuff() {
	// basic
	notify('Doing stuff');
	// modify default
	notify('Successful', { type: 'success', position: 'top', duration: 2000 });
	// include callback
	notify('Execute callback ?', { onAction: () =&gt; this.callback(), actionText: 'Proceed' })
}
