const popover = new PopoverView({
	template: `&lt;div&gt;
	&lt;p&gt;Do stuff.&lt;/p&gt;
	&lt;p&gt;Do you confirm ?&lt;/p&gt;
&lt;/div&gt;`,
	// optional: popover buttons
	buttons: [
	{
		callback: () =&gt; this.confirm(),
		textContent: 'Yes'
	},
	{
		callback: () =&gt; { return },
		textContent: 'No'
	}]
	// optional: callback on popover close
	onClose: () =&gt; { console.log('Popover closed') }
	// optional: view name. default: 'popover'
	viewName: 'popoverConfirm'
});

// call inherited render()
popover.render();
