content:
// Utility classes prefix : 'u-'

// $name = smallest || small || medium || large || largest
// font size
@each $name, $size in $font-sizes { .u-font-#{$name} }
// padding
@each $name, $space in $spacings { .u-padd-x-#{$name} }
@each $name, $space in $spacings { .u-padd-y-#{$name} }
//margin
@each $name, $space in $spacings { .u-marg-x-#{$name} }
@each $name, $space in $spacings { .u-marg-y-#{$name} }
//border-radius
// $name = small || medium || large || pill
@each $name, $radius in $border-radius { .u-rounded-#{$name} }
// box-shadow
// $name = small | medium || large
@each $name, $shadow in $shadows { .u-shadow-#{$name} }
// transition
// $name = fast || normal || slow
@each $name, $transition in $transitions { .u-transition-#{$name} }

