// called before each test
setupJsdom(htmlPath = 'dist/src/index.html', options = {
		runScripts = false,     // or true ("dangerously") if needed
		resources = false,      // or true ("usable") if needed
		freezeGlobals = false,
		pretendToBeVisual = true,
	}
)
// expose globals
global.window
global.document

// DOM globals
Object.getOwnPropertyNames(dom.window)
	.filter(prop =&gt; !(prop in global))
	.forEach(prop =&gt; {
		global[prop] = dom.window[prop];
})

global.localStorage
// browser APIs
window.matchMedia
window.scrollTo
window.requestAnimationFrame
window.cancelAnimationFrame
window.IntersectionObserver
window.ResizeObserver

// called after each test
teardownJsdom()

// globals
global.setupJsdom
global.teardownJsdom
global.resetDom
global.flushPromises

global.$ = (selector) =&gt; {}
global.$$ = (selector) =&gt; {}

global.click = (el) =&gt; {}
global.type = (el, text) =&gt; {}
