content:
gzip on;
gzip_vary on;
gzip_static on;
gzip_proxied any;
# balance between CPU usage / file size reduction
gzip_comp_level 6;
# do not compress files smaller than 1kb
gzip_min_length 1000;

gzip_types
		text/plain
		text/css
		text/xml
		text/javascript
		application/json
		application/javascript
		application/xml+rss
		application/rss+xml
		font/truetype
		font/opentype
		application/vnd.ms-fontobject
		image/svg+xml;
