content:
// Utility classes prefix : 'u-'

// font size
@each $name, $size in $font-sizes {
	.u-font-#{$name}
}

// padding
@each $name, $space in $spacings {
	.u-padd-x-#{$name}
}
@each $name, $space in $spacings {
	.u-padd-y-#{$name}
}

//margin
@each $name, $space in $spacings {
	.u-marg-x-#{$name}
}

@each $name, $space in $spacings {
	.u-marg-y-#{$name}
}

//border-radius
@each $name, $radius in $border-radius {
	.u-rounded-#{$name}
}

// box-shadow
@each $name, $shadow in $shadows {
	.u-shadow-#{$name}
}

// transition
@each $name, $transition in $transitions {
	.u-transition-#{$name}
}

