import { ComponentConfig, View } from '../../../core/index.js';
import { TaskComponent } from '../../components/index.js';
import { TasksService } from '../../services/index.js';

const template = `
&lt;div data-catalog="task-catalog" class="u-flex-center u-flex-column"&gt;&lt;/div&gt;
`;

export class TaskCatalogView extends View {
	private _tasks: Task[] = [];

	constructor() {
		super({template});
	}

	public onEnter() {
		this._tasks = TasksService.tasks;
	}

	public childConfigs(): ComponentConfig[] {
		const tasksContainer = document.getElementById('task-catalog');
		return this.catalogConfig({
			array: this._tasks,
			elementName: 'task',
			container: tasksContainer,
			component: TaskComponent
		});
	}

}
