content:
.task-catalog__add-task-button {
	transition: ease-in-out all .3s;
	background-color: rgba($text-color, .7);
	color: $background-color;
	font-size: 1.5em;
	font-style: italic;
	padding: .5em;
	margin: 1em;
	border-radius: 1em;
	&:hover, &:focus {
		background-color: $text-color;
		outline: none;
	}
}
