// In template, on top :
&lt;div data-component="add-task"&gt;&lt;/div&gt;

// subscribe to the newly created event to trigger rendering
constructor() {
	AppEventBus.subscribe('task-updated', () => this.render());
}

protected override forceRender(): void {
	this._tasks = TasksService.tasks;
	return super.forceRender();
}

public childConfigs(): ComponentConfig[] {
		const tasksContainer = document.getElementById('task-catalog');

		const addTaskConfig: ComponentConfig = {
			selector: `add-task`,
			factory: (el) => new AddTaskComponent(el)
		};

		const tasksConfigs = this.catalogConfig({
			array: this._tasks,
			elementName: 'task',
			container: tasksContainer,
			component: TaskComponent
		});

		return [ addTaskConfig, ...tasksConfigs ];
	}
