// Use AppEventBus so that AppRouter handles dynamic DOM update
	private emitReload() {
		AppEventBus.emit('reload');
	}

	private tasksUpdated() {
		this.emitReload();
	}

	public addTask() {
		const id = this._tasks.length === 0 
			? 0 
			: Math.max(...this._tasks.map(task => task.id)) + 1;

		const newTask: Task = {
			id: id,
			name: `Untitled ${id + 1}`,
			content: '',
			date: new Date(),
		};

		this._tasks.push(newTask);
		this.tasksUpdated();
	}
