import { Component } from '../../../core/index.js';
const templateFn = () => `&lt;div style="min-width:75vw;height:100vh;margin-top:1em;text-align:center;"&gt;
	&lt;div class="u-flex-center u-flex-column"&gt;
		&lt;p&gt;${task.name}&lt;/p&gt;
		&lt;p
			data-pipe="capitalize"
		&gt;${task.content}&lt;/p&gt;
	  &lt;p 
	    class="u-font-small u-italic"
	    data-pipe="date:en-US,long,time|capitalizeAll"
	  &gt;${task.date}&lt;/p&gt;
	&lt;/div&gt;
&lt;/div&gt;`;

export class TaskDetailsComponent extends Component {
	private _task: Task;

	constructor(mountTarget: HTMLElement, task: Task) {
		super({templateFn, config: task, mountTarget});
		this._task = task;
	}
}
