private fetchTasks() {
	this._tasks = localStorage.tasks 
		? JSON.parse(localStorage.tasks)
		: [];
}

// call it in the constructor and remove the default task
constructor() {
	super();
	this.registerCleanup(this.clear);
	this.fetchTasks();
}

private setTasks() {
	localStorage.setItem('tasks', JSON.stringify(this._tasks));
}

// call it in tasksUpdated method
private tasksUpdated() {
	this.setTasks();
	this.emitReload();
}
