import { notify, NotifyOptions } from '../../../libs/index.js';

private notification(
	message: string, 
	type: NotifyOptions['type'] = 'success'
) {
	notify(message, { type: type });
}

public addTask() {
	// existing code...
	this.notification('New task created.');
}

public deleteTask(id: number) {
	// existing code...
	this.notification('Task deleted.')
}

public updateTask(task: Task) {
	if (isNaN(Date.parse(task.date.toString()))) {
		this.notication('Issue with "Date" property when updating task.', 'error');
		return;
	};

	// existing code...

	this.notication('Task updated.')
}
